
-- Drop existing tables to start fresh
DROP TABLE IF EXISTS order_items;
DROP TABLE IF EXISTS orders;
DROP TABLE IF EXISTS materials_inventory;
DROP TABLE IF EXISTS users;

-- Create orders table with EXACT structure from your backup
CREATE TABLE orders (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    order_number TEXT NOT NULL UNIQUE,
    shopify_order_id TEXT,
    customer_name TEXT,
    customer_email TEXT,
    customer_phone TEXT,
    customer_address TEXT,
    customer_city TEXT,
    customer_state TEXT,
    customer_zip TEXT,
    customer_country TEXT,
    order_type TEXT DEFAULT 'retail',
    is_reseller BOOLEAN DEFAULT 0,
    reseller_nickname TEXT,
    status TEXT DEFAULT 'ordered',
    order_date DATETIME,
    deadline DATETIME,
    notes TEXT,
    progress INTEGER DEFAULT 0,
    specifications TEXT,
    status_change_dates TEXT,
    build_date DATETIME,
    archived BOOLEAN DEFAULT 0,
    tracking_number TEXT,
    tracking_company TEXT,
    tracking_url TEXT,
    shipped_date DATETIME,
    estimated_delivery_date DATETIME,
    delivery_status TEXT,
    delivered_date DATETIME,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    is_urgent BOOLEAN DEFAULT 0
);

-- Create order_items table with EXACT structure from your backup
CREATE TABLE order_items (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    order_id INTEGER NOT NULL,
    serial_number TEXT,
    item_type TEXT,
    item_size TEXT,
    tuning_type TEXT,
    color TEXT,
    weight REAL,
    craftsperson TEXT,
    order_number TEXT,
    order_date DATETIME,
    deadline DATETIME,
    build_date DATETIME,
    bag_size TEXT,
    box_size TEXT,
    shopify_line_item_id TEXT,
    specifications TEXT,
    status TEXT DEFAULT 'ordered',
    progress INTEGER DEFAULT 0,
    status_change_dates TEXT,
    is_archived BOOLEAN DEFAULT 0,
    archived_reason TEXT,
    workshop_notes TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (order_id) REFERENCES orders(id)
);

-- Create materials_inventory table
CREATE TABLE materials_inventory (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    material_name TEXT NOT NULL,
    material_type TEXT,
    bag_type TEXT,
    size TEXT,
    quantity INTEGER DEFAULT 0,
    reorder_point INTEGER DEFAULT 0,
    ordered INTEGER DEFAULT 0,
    expected_delivery DATETIME,
    order_date DATETIME,
    order_reference TEXT,
    display_order INTEGER DEFAULT 0,
    last_updated DATETIME DEFAULT CURRENT_TIMESTAMP,
    notes TEXT
);

-- Create users table
CREATE TABLE users (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    username TEXT NOT NULL UNIQUE,
    email TEXT NOT NULL UNIQUE,
    password_hash TEXT NOT NULL,
    role TEXT DEFAULT 'user',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);


